// Type declarations for side-effect style imports so TypeScript stops
// complaining when importing CSS files (e.g. `import './globals.css'`).
// Place this file at the project root so the compiler picks it up.

declare module '*.css';
declare module '*.scss';
declare module '*.sass';
declare module '*.less';
declare module '*.module.css';
declare module '*.module.scss';
declare module '*.module.sass';

